function pairwise(arr, arg) {
  var sum = 0;
  var used = [];
  for(var i = 0; i < arr.length-1; i++){
    for(var j = i+1; j < arr.length; j++){
      if(arr[i] + arr[j] == arg){
        if(used.includes(i) || used.includes(j, j)){
          continue;
        } else {
            sum += (i+j);
            used.push(i, j);
          i++;
          j++;
        }

      }
    }
  }
  return sum;
}

pairwise([1,4,2,3,0,5], 7);
