    // Only change code below this line
      if (head === null) return;
      let previous;
      let current = head;
      while (current.next !== null && current.element !== element) {
          previous = current;
          current = current.next;
      }
      if (previous) {
          previous.next = current.next;
      } else {
          head = current.next;
      }
      --length;
